import React from 'react';
import { motion } from 'framer-motion';
import { Heart, ArrowUp } from 'lucide-react';

const Footer: React.FC = () => {
  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <footer className="relative py-12 border-t border-white/10 bg-transparent">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-8 pb-8 border-b border-white/10">
          {/* Left: Portfolio Title & Description */}
          <div className="flex-1 min-w-[200px]">
            <h3 className="text-2xl font-bold bg-gradient-to-r from-primary-600 to-purple-600 bg-clip-text text-transparent mb-2">
              Portfolio
            </h3>
            <p className="text-gray-400 text-sm max-w-xs">
              A passionate developer building user-friendly solutions with modern web technologies.
            </p>
          </div>
          {/* Center: Quick Links */}
          <div className="flex-1 min-w-[200px] text-center">
            <h4 className="text-white font-semibold mb-4">Quick Links</h4>
            <div className="flex flex-wrap justify-center gap-4 text-sm">
              {[
                { name: 'Home', href: '#home' },
                { name: 'About', href: '#about' },
                { name: 'Experience', href: '#experience' },
                { name: 'Projects', href: '#projects' },
                { name: 'Skills', href: '#skills' },
                { name: 'Contact', href: '#contact' }
              ].map((link) => (
                <a
                  key={link.name}
                  href={link.href}
                  className="text-gray-300 hover:text-white transition-colors duration-300"
                >
                  {link.name}
                </a>
              ))}
            </div>
          </div>
          {/* Right: Back to Top Button */}
          <div className="flex-1 min-w-[200px] flex justify-center md:justify-end">
            <motion.button
              onClick={scrollToTop}
              whileHover={{ scale: 1.07, y: -3 }}
              whileTap={{ scale: 0.95 }}
              className="inline-flex items-center gap-2 px-8 py-4 bg-gradient-to-r from-primary-600 to-purple-600 rounded-full text-white font-semibold shadow-md hover:shadow-lg transition-all duration-300"
            >
              <ArrowUp size={20} />
              <span>Back to Top</span>
            </motion.button>
          </div>
        </div>
        {/* Bottom Row */}
        <div className="flex flex-col md:flex-row justify-between items-center pt-8 text-sm text-gray-400">
          <div>
            © {new Date().getFullYear()} Portfolio. All rights reserved.
          </div>
          <div className="flex items-center gap-1 mt-2 md:mt-0">
            <span>Made with</span>
            <Heart size={16} className="text-red-500 animate-pulse" />
            <span>React & TypeScript</span>
          </div>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
