import React from 'react';
import { motion } from 'framer-motion';
import { Briefcase } from 'lucide-react';
import styles from './Experience.module.css';

const Experience: React.FC = () => {
  const experiences = [
    {
      title: 'Software Engineering Intern',
      company: 'Yapı Kredi Teknoloji, Remote',
      period: 'February 2025 - Present',
      side: 'left',
    },
    {
      title: 'Software Engineering Intern',
      company: 'JIOT, Istanbul, Turkey',
      period: 'June 2024 - July 2024',
      side: 'right',
    },
  ];

  return (
    <section id="experience" className={styles.experienceSection}>
      <h2 className={styles.title}>Experience</h2>
      <div className={styles.timeline}>
        <div className={styles.timelineLine}></div>
        {experiences.map((exp, idx) => (
          <div
            key={idx}
            className={
              styles.timelineItem + ' ' + (exp.side === 'right' ? styles.right : '')
            }
            style={{ marginTop: idx === 0 ? '0' : '4rem' }}
          >
            {/* Kart */}
            <motion.div
              className={styles.card + ' ' + (idx === 0 ? styles.active : '')}
              initial={{ opacity: 0, y: 40 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.7, delay: idx * 0.15 }}
              viewport={{ once: true }}
            >
              <div className="jobTitle">{exp.title}</div>
              <div className="company">{exp.company}</div>
            </motion.div>
            {/* Timeline Icon ve Tarih */}
            <div className={styles.timelineIcon}>
              <Briefcase />
            </div>
            <div className="dates" style={{ color: '#22c55e', fontWeight: 600, margin: '0 1rem', minWidth: 180 }}>
              {exp.period}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Experience;
