import React from 'react';
import { motion } from 'framer-motion';
import { Calendar, MapPin, ExternalLink } from 'lucide-react';

const Experience: React.FC = () => {
  const experiences = [
    {
      title: 'Senior Full Stack Developer',
      company: 'Tech Company',
      period: '2022 - Günümüz',
      location: 'İstanbul, Türkiye',
      description: [
        'Modern web uygulamaları geliştirme ve sürdürme',
        'React, Node.js ve PostgreSQL teknolojileri ile çalışma',
        'Takım liderliği ve mentorluk yapma',
        'Mikroservis mimarisi tasarımı ve implementasyonu'
      ],
      technologies: ['React', 'Node.js', 'PostgreSQL', 'Docker', 'AWS']
    },
    {
      title: 'Full Stack Developer',
      company: 'Startup Company',
      period: '2020 - 2022',
      location: 'İstanbul, Türkiye',
      description: [
        'E-ticaret platformu geliştirme',
        'REST API tasarımı ve implementasyonu',
        'Performans optimizasyonu ve SEO iyileştirmeleri',
        'Agile metodolojisi ile çalışma'
      ],
      technologies: ['Vue.js', 'Express.js', 'MongoDB', 'Redis', 'Nginx']
    },
    {
      title: 'Frontend Developer',
      company: 'Digital Agency',
      period: '2018 - 2020',
      location: 'İstanbul, Türkiye',
      description: [
        'Responsive web siteleri ve web uygulamaları geliştirme',
        'UI/UX tasarım prensiplerini uygulama',
        'Cross-browser uyumluluğu sağlama',
        'Client projelerinde aktif rol alma'
      ],
      technologies: ['JavaScript', 'HTML/CSS', 'Sass', 'Webpack', 'Git']
    }
  ];

  return (
    <section id="experience" className="py-20 relative">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold gradient-text mb-4">
            Deneyim
          </h2>
          <p className="text-xl text-gray-400 max-w-2xl mx-auto">
            Kariyer yolculuğum ve profesyonel deneyimlerim
          </p>
        </motion.div>

        <div className="relative">
          {/* Timeline Line */}
          <div className="absolute left-4 md:left-1/2 transform md:-translate-x-1/2 top-0 bottom-0 w-0.5 bg-gradient-to-b from-primary-500 to-purple-500"></div>

          <div className="space-y-12">
            {experiences.map((exp, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
                whileInView={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.8, delay: index * 0.2 }}
                viewport={{ once: true }}
                className={`relative flex items-center ${
                  index % 2 === 0 ? 'md:flex-row' : 'md:flex-row-reverse'
                }`}
              >
                {/* Timeline Dot */}
                <div className="absolute left-4 md:left-1/2 transform md:-translate-x-1/2 w-4 h-4 bg-gradient-to-r from-primary-500 to-purple-500 rounded-full border-4 border-dark-800"></div>

                {/* Content Card */}
                <div className={`ml-12 md:ml-0 md:w-5/12 ${
                  index % 2 === 0 ? 'md:mr-auto md:pr-8' : 'md:ml-auto md:pl-8'
                }`}>
                  <motion.div
                    whileHover={{ scale: 1.02 }}
                    className="p-6 glass-effect rounded-xl card-hover"
                  >
                    {/* Header */}
                    <div className="mb-4">
                      <h3 className="text-xl font-semibold text-white mb-2">
                        {exp.title}
                      </h3>
                      <h4 className="text-lg text-primary-400 font-medium mb-2">
                        {exp.company}
                      </h4>
                      
                      <div className="flex items-center space-x-4 text-sm text-gray-400 mb-3">
                        <div className="flex items-center space-x-1">
                          <Calendar size={16} />
                          <span>{exp.period}</span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <MapPin size={16} />
                          <span>{exp.location}</span>
                        </div>
                      </div>
                    </div>

                    {/* Description */}
                    <div className="mb-4">
                      <ul className="space-y-2">
                        {exp.description.map((item, itemIndex) => (
                          <motion.li
                            key={itemIndex}
                            initial={{ opacity: 0, x: -20 }}
                            whileInView={{ opacity: 1, x: 0 }}
                            transition={{ delay: itemIndex * 0.1 }}
                            viewport={{ once: true }}
                            className="flex items-start space-x-2 text-gray-300"
                          >
                            <span className="text-primary-400 mt-1">•</span>
                            <span>{item}</span>
                          </motion.li>
                        ))}
                      </ul>
                    </div>

                    {/* Technologies */}
                    <div>
                      <h5 className="text-sm font-medium text-gray-400 mb-2">
                        Kullanılan Teknolojiler:
                      </h5>
                      <div className="flex flex-wrap gap-2">
                        {exp.technologies.map((tech, techIndex) => (
                          <motion.span
                            key={tech}
                            initial={{ opacity: 0, scale: 0.8 }}
                            whileInView={{ opacity: 1, scale: 1 }}
                            transition={{ delay: techIndex * 0.05 }}
                            viewport={{ once: true }}
                            className="px-3 py-1 bg-primary-500/20 text-primary-300 rounded-full text-xs font-medium"
                          >
                            {tech}
                          </motion.span>
                        ))}
                      </div>
                    </div>
                  </motion.div>
                </div>
              </motion.div>
            ))}
          </div>
        </div>

        {/* Education Section */}
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="mt-20"
        >
          <h3 className="text-3xl font-bold text-white text-center mb-12">
            Eğitim
          </h3>
          
          <div className="grid md:grid-cols-2 gap-8">
            {[
              {
                degree: 'Bilgisayar Mühendisliği',
                school: 'İstanbul Teknik Üniversitesi',
                period: '2014 - 2018',
                description: 'Yazılım geliştirme ve algoritma analizi üzerine odaklanarak mezun oldum.'
              },
              {
                degree: 'Web Geliştirme Sertifikası',
                school: 'Udemy',
                period: '2019',
                description: 'Modern web teknolojileri ve full-stack geliştirme konularında sertifika aldım.'
              }
            ].map((edu, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.2 }}
                viewport={{ once: true }}
                whileHover={{ scale: 1.02 }}
                className="p-6 glass-effect rounded-xl card-hover"
              >
                <h4 className="text-xl font-semibold text-white mb-2">
                  {edu.degree}
                </h4>
                <h5 className="text-lg text-primary-400 mb-2">
                  {edu.school}
                </h5>
                <p className="text-gray-400 text-sm mb-3">
                  {edu.period}
                </p>
                <p className="text-gray-300">
                  {edu.description}
                </p>
              </motion.div>
            ))}
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Experience;
